import React, { useState } from "react";

function ImageModal({
  projectImage,
  projectImageAlt,
  imageDescription,
  modalDisplay,
  close,
}) {
  return (
    <div
      className={`${modalDisplay} fixed flex content-center align-center m-auto w-1/2 h-1/2 bg-white border-4 border-[#305065] rounded drop-shadow`}
    >
      <img src={projectImage} alt={imageDescription} />
    </div>
  );
}

export default ImageModal;
